import React, { Component , useState, useEffect} from 'react';
import AudioAnalyser from './AudioAnalyser';

const App=()=> {
  const [buttonName, setButtonName] = useState("Play");

  const [audio, setAudio] = useState();

  useEffect(() => {
    if (a) {
      a.pause();
      a = null;
      setButtonName("Play");
    }
    if (audio) {
      a = new Audio(audio);
      a.onended = () => {
        setButtonName("Play");
      };
    }
  }, [audio]);

  const handleClick = () => {
    if (buttonName === "Play") {
      a.play();
      setButtonName("Pause");
    } else {
      a.pause();
      setButtonName("Play");
    }
  };

  const addFile = (e) => {
    if (e.target.files[0]) {
      setAudio(URL.createObjectURL(e.target.files[0]));
    }
  };

  // getMicrophone=async()=> {
  //   const audio = await navigator.mediaDevices.getUserMedia({
  //     audio: true,
  //     video: false
  //   });
  //   setAudio(audio)
  // }

  stopMicrophone=()=> {
    audio.getTracks().forEach(track => track.stop());
    setAudio(null)
  }

  toggleMicrophone=()=> {
    if (this.state.audio) {
      stopMicrophone();
    } else {
      const audio = navigator.mediaDevices.getUserMedia({
        audio: true,
        video: false
      });
      setAudio(audio)
      // getMicrophone();
    }
  }

    return (
      <div className="App">
        <div className="controls">
          <button onClick={()=>toggleMicrophone()}>
            {audio ? 'Stop microphone' : 'Get microphone input'}
          </button>
        </div>
        {audio ? <AudioAnalyser audio={audio} /> : ''}
      </div>
    );
}

export default App;
